mapper.select:
    select 
    
    from ln_attach_info
    
   	
		AND ID = #{id,jdbcType=BIGINT}
	
   	
		 AND APP_ID = #{appId,jdbcType=BIGINT}
	
   	
		 AND SOURCE = #{source}
		
   	
		 AND CHOOSE_TYPE = #{chooseType}
	
   	
		 AND ATTACH_TYPE = #{attachType}
		
   	
		 AND EXAMINE_SEQ_NO = #{examineSeqNo}
		
   	
		 AND EXAMINE_BATCH_NO = #{examineBatchNo}
		
	
	ORDER BY CREATEDON desc

    select 
    
    from ln_attach_info
    
   	
		AND ID = #{id,jdbcType=BIGINT}
	
   	
		 AND APP_ID = #{appId,jdbcType=BIGINT}
	
   	
		 AND SOURCE = #{source}
		
   	
		 AND CHOOSE_TYPE = #{chooseType}
	
	
	GROUP BY ATTACH_TYPE

SELECT DISTINCT
FROM bs_attach att, bs_attach patt, ln_attach_info ainfo WHERE patt.id = att.parent_id AND ainfo.attach_type = att.code AND patt.parent_id IS NULL
AND ainfo.app_id = #{appId,jdbcType=BIGINT}
AND ainfo.source = #{source}
AND patt.choose_type = #{chooseType}
GROUP BY ainfo.ATTACH_TYPE ORDER BY patt.`CODE`
