mapper.select:
    select 
    
    from ln_app_cust_info
    
   	
		AND ID = #{id,jdbcType=BIGINT}
	
   	
		 AND APP_ID = #{appId,jdbcType=BIGINT}

select
FROM ln_app_cust_info cust LEFT JOIN sm_district dis ON dis.districtId = cust.HOUSEHOLD_TOWN LEFT JOIN sm_district dis2 ON dis2.districtId = cust.LIVE_TOWN LEFT JOIN sm_district dis3 ON dis3.districtId = cust.UNIT_TOWN
AND cust.ID = #{id,jdbcType=BIGINT}
AND cust.APP_ID = #{appId,jdbcType=BIGINT}
